void solve(int turn = 0)
{
	int parity = 0;
	for (int i = 0; i < n; i++)
		parity ^= (a[i] & 1);
	if (n & 1)
	{
		if (!parity)
		{
			cout << (turn ? "Second" : "First") << endl;
			return;
		}
		else
		{
			int cnt = 0;
			for (int i = 0; i < n; i++)
				cnt += (a[i] & 1);
			if (cnt > 1)
			{
				cout << ((1 - turn) ? "Second" : "First") << endl;
				return;
			}
			else
			{
				for (int i = 0; i < n; i++)
