#define int long long
using namespace std;

int N, A[100000];
vector< int > g[100000];

int rec(int idx, int par = -1)
{
  if(g[idx].size() == 1) return (A[idx]);
  priority_queue< int > que;
  que.push(0);
  int sum = 0;
  for(auto &to : g[idx]) {
    if(to == par) continue;
    auto get = rec(to, idx);
    sum += get;
    que.emplace(get);
  }
  while(que.size() > 1) {
    int p = que.top();
    que.pop();
    int q = que.top();
    que.pop();
    que.push(p - q);
  }
  int zan = (sum - que.top()) / 2;

  //cout << idx + 1 << " " << zan << endl;
  if((sum+1)/2 < A[idx] || sum - zan > A[idx]) throw 1LL;

  return (sum - (sum - A[idx]) * 2);
}

signed main()
{
  cin >> N;
  for(int i = 0; i < N; i++) cin >> A[i];
  for(int i = 0; i < N - 1; i++) {
    int a, b;
    cin >> a >> b;
    --a, --b;
    g[a].push_back(b);
    g[b].push_back(a);
  }

  int root = -1;
  for(int i = 0; i < N; i++) {
    if(g[i].size() >= 2) root = i;
  }

  if(root == -1) {
    if(A[0] == A[1]) cout << "YES" << endl;
    else cout << "NO" << endl;
  } else {
    try {
      if(rec(root) != 0) throw 1LL;
      cout << "YES" << endl;
    } catch(int x) {
      cout << "NO" << endl;
    }
  }
}

