        for (int i = 0; i < N; i++) {
            sum += (a[i] - 1);
            if (a[i] == 1) { one++; }
            (a[i] % 2 == 0 ? even : odd)++;
        }
        if (one > 0) { return sum % 2 == 1; }
        if (even % 2 == 1) { return true; }
        if (odd > 0) { return false; }
        ll g               = 0;
        std::vector<ll> na = a;
        for (int i = 0; i < N; i++) { g = gcd(g, na[i] % 2 ? na[i] : --na[i]); }
        for (int i = 0; i < N; i++) { na[i] /= g; }
        return not self(self, na);
    };
    std::cout << (win(win, A) ? "First" : "Second") << std::endl;
    return 0;
}

