bool solve(vector<long long> &A){
    long long O, E;
    O = E = 0;
    for(auto a : A)
        if(a & 1)
            O++;
        else
            E++;
    if(E & 1)
        return 1;
    if(O > 1)
        return 0;
    for(long long i= 0; i < A.size(); i++)
        if(A[i] == 1)
            return 0;
        else
            A[i] /= 2;
