bool solve(){
	LL gcds=A[0]/2*2;
	LL sum=0;
	int odds=0;
	bool isEnd=false;
	REP(i,N){
		sum+=A[i]-1;	
		if(A[i]%2) odds++;		
		if(A[i]==1) isEnd=true;
		else gcds=gcd(gcds,A[i]/2*2);
