int simulate()
{
    c[0] = c[1] = 0;
    for(int i = 1; i <= n; ++i) ++c[a[i]&1];
    if(c[0]&1) return 1;
    if((~c[0]&1)&&c[1] >= 2) return 0;
    for(p = 1; p <= n&&(~a[p]&1); ++p);
    if(a[p] == 1)
    {
        for(int i = 1; i <= n; ++i) s ^= (a[i]-1)&1;
        return s;
    }
    --a[p], d = a[1]; 
    for(int i = 2; i <= n; ++i) d = gcd(d, a[i]);
    for(int i = 1; i <= n; ++i) a[i] /= d;
    return simulate()^1;
}
