const int N = 1e5 + 100;

int n;
int a[N];

bool solve()
{
    int odd, even;
    odd = even = 0;
    rep(i, n) if(a[i] & 1) ++odd; else ++even;
    if(odd == 1)
    {
        if(even & 1) return 1;
        rep(i, n) if(a[i] & 1){ if(a[i] == 1) return 0; a[i]--; break; }
        int gcd = a[0];
        rep(i, n) gcd = __gcd(gcd, a[i]);
        rep(i, n) a[i] /= gcd;
        return solve() ^ 1;
    }
    return (even & 1);
}

