inline bool dfs() {
	int cnt1 = 0, cnt2 = 0, flag = 0;
	For(i, 1, n) {
		if(A[i] == 1) flag = 1;
		if(A[i] & 1) ++ cnt1;
		else ++ cnt2;
	}

