int gcd(int a,int b){
	if (b==0){
		return a;
	}
	return gcd(b,a%b);
}

bool solve(){
	bool win=true;
	while(1){
		int os=0,es=0;
		REP(i,N){
			if (A[i]%2==1){
				os++;
			}else{
				es++;
			}
