void build(int v, int s, int e) {
	if (e - s == 1) {
		seg[v] = a[s];
		return;
	}
	int mid = (s + e) / 2;
	build(v << 1, s, mid);
	build(v << 1 | 1, mid, e);
	seg[v] = __gcd(seg[v << 1], seg[v << 1 | 1]);
}

void get(int l, int r, int v, int s, int e) {
	if (r <= s || e <= l)
		return;
	if (l <= s && e <= r) {
		if (gc)
			gc = __gcd(gc, seg[v]);
		else
			gc = seg[v];
		return;
	}
	int mid = (s + e) / 2;
	get(l, r, v << 1, s, mid);
	get(l, r, v << 1 | 1, mid, e);
}

