  while (a && b) {
    if (a > b) a %= b;
    else b %= a;
  }
  return a + b;
}
bool go() {
  int odd = 0, even = 0;
  for (int i = 1; i <= n; ++i) {
    if (arr[i] & 1) odd++;
    else even++;
  }
  if (even & 1) {
    // the state of even integers goes from odd to even to odd.... until 1 1 1 1 1..1
    return true;
  }
  if (odd >= 2) {
    // we'll end up at the above case with opponent playing
    return false;
  }
 // cout << odd << " " << even << endl;
