bool solve(int N, vector<ll>& A) {
    cerr << A << endl;
    if (N == 1) return false;
    ll S = accumulate(A.begin(), A.end(), 0LL);
    if (N % 2 == 0) return (S - N) % 2 != 0;
    if (S % 2 == 0) return true;

    // 奇数を前に持っていく
    sort(A.begin(), A.end(), [](int a, int b) { return a % 2 > b % 2; });
    // 奇数が複数個あれば負け
    if (A[0] == 1 || A[1] % 2 != 0) return false;
    // そうでなければ、奇数を減らしてワンチャンに賭ける
    --A[0];
    ll g = accumulate(A.begin(), A.end(), 0LL, gcd<ll>);
    for (auto& a : A) a /= g;
    return !solve(N, A);
}

int main() {
    int N;
    cin >> N;
    vector<ll> A(N);
    for (auto& a : A) cin >> a;
    cout << (solve(N, A) ? "First" : "Second") << endl;
    return 0;
}

