int solve(vector<int> a)
{
	int odd = 0 , even = 0 , f = 0;

	for(auto x : a)
	{
		if(x % 2)
			odd++;
		else
			even++;

		if(x == 1)
			f = 1;
	}

	if(f)
		return even % 2;

