    for (int i: v) if (i % 2 == 0) e ++;
    // cout << e << "\n"
    if (e&1) return 1;
    if (e+1 == sz(v)) {
        for (int& i: v) {
            if (i % 2 == 1) i --;
            if (i == 0) return 0;
        }
        int g = 0; 
        for (int& i: v) g = __gcd(g,i);
        for (int& i: v) i /= g;
        return !solve(v);
    }
    return 0;
}

int main() {
    ios_base::sync_with_stdio(0); cin.tie(0);
    int n; cin >> n; vi v(n); F0R(i,n) cin >> v[i];
    if (solve(v)) cout << "First";
    else cout << "Second";
}

