const int maxn = 1e5 + 100;

int n;
int a[maxn];

bool read() {
  if (scanf("%d", &n) < 1) {
    return false;
  }
  for (int i = 0; i < n; i++) {
    scanf("%d", a + i);
  }
  return true;
}

void solve() {
  if (n == 1) {
    printf("Second\n");
    return;
  }
  int it = 0;
  for (; ; it++) {
    int cnteven = 0;
    bool fail = true;
    int posodd = -1;
    for (int i = 0; i < n; i++) {
      if (a[i] != 1) {
        fail = false;
      }
      if (a[i] % 2 == 0) {
        cnteven++;
      } else {
        posodd = i;
      }
    }
    if (fail) {
      it++;
      break;
    }
    if (cnteven % 2 == 1) {
      break;
    }
    if (cnteven == n - 1) {
      if (a[posodd] == 1) {
        it++;
        break;
      }
      a[posodd]--;
      int g = a[0];
      for (int i = 0; i < n; i++) {
        g = gcd(a[i], g);
      }
      for (int i = 0; i < n; i++) {
        a[i] /= g;
      }
    } else {
      it++;
      break;
    }
  }
  if (!(it & 1)) {
    printf("First\n");
  } else {
    printf("Second\n");
  }
}

