    if (n == 1) {
        if (a[0] != 1) return win(1);
        else return lose(1);
    }
    int x = n & 1;
    int cnt = 0;
    while (cnt++) {
        int y = 0;
        for (int i = 0; i < n; i++) y = (y + a[i]) & 1;
        if (x != y && y == 1) return win(cnt&1);
        if (x == y && y == 0) return lose(cnt&1);
        int sum = 0;
        for (int i = 0; i < n; i++) {
            sum += a[i] & 1;
            sum += a[i] == 1;
        }
        if (sum > 1) break;
        while (true) {
            int s = 0;
            for (int i = 0; i < n; i++) {
                a[i] /= 2;
                s += a[i] & 1;
                s += a[i] == 1;
            }
            if (s) break;
        }
        bool end = false;
        for (int i = 0; i < n; i++) end |= a[i] == 1;
        if (end) {
            cnt++; break;
        }
    }
    int y = 0;
    for (int i = 0; i < n; i++) y = (y + a[i]) & 1;
    if (x != y && y == 1) return win(cnt&1);
    else if (x == y && y == 0) return lose(cnt&1);
    else lose(cnt & 1);
    return 0;
}

