bool solve(bool win) {
    int ha = 0, cnt[2] = {0, 0};
    for (int i = 0; i < n; i++) {
        cnt[a[i] % 2]++;
        if (a[i] == 1)
            ha++;
    }   
    if (cnt[0] % 2)
        return win;
    if (cnt[1] > 1)
        return !win;
    if (ha)
