ll segn;  // array size
ll t[2 * mn];
ll a[mn];

void build() {  // build the tree
  ll n=segn;
  for (ll i = n - 1; i > 0; --i) t[i] = gcd(t[i<<1], t[i<<1|1]);
}

ll query(ll l, ll r) {  // sum on interval [l, r]
  ll n=segn;
  r++;
  ll res = 0;
  for (l += n, r += n; l < r; l >>= 1, r >>= 1) {
    if (l&1) res = gcd(res,t[l++]);
    if (r&1) res = gcd(res,t[--r]);
  }
  return res;
}
bool solve(vector<ll> a) {
  ll n=a.size();
  if (n==1) {
  	return a[0]!=1;
  }
  ll sum=0;
  for (ll i=0;i<n;i++) {
    sum+=a[i];
  }
  segn=n;
  for (ll i=0;i<n;i++) {
    t[n+i]=a[i];
  }
  build();
  vector<pll> info;
  for (ll x=0;x<n;x++) {
  	if (a[x]==1) continue;
    ll g=gcd(gcd(query(0,x-1),query(x+1,n-1)),a[x]-1);
    ll newsum=(sum-1)/g;
    ll steps=newsum-n;
    if (steps%2==0) {
    	ll numcand=0,cand=-1;
    	for (ll y=0;y<n;y++) {
    		ll val=(x==y)?(a[y]-1)/g:a[y]/g;
    		if (val%2==0) {}
    		else if (val!=1) {if (numcand==1) break; numcand=1;}
    		else if (val==1) {numcand=-INF; break;}
    	}
    	if (numcand==1) {
    		info.PB(MP(x,g));
    	}
    	else return true;
    }
   }
  for (auto &got:info) {
  	ll x=got.first,g=got.second;
    vector<ll> b;
    for (ll y=0;y<n;y++) {
    	if (y==x) b.PB((a[y]-1)/g);
    	else b.PB(a[y]/g);
    }
    if(!solve(b)) return true;
  }
  return false;
}
