struct GCD{
    using T = long long;
    T operator()(const T &a, const T &b){return gcd(a,b);}
    static constexpr T id(){return T(0);};
};

template<typename M>
struct SegmentTree{
    using Val = typename M::T;
    M calc;
    vector<Val> data;
    int n;
    explicit SegmentTree(int size){
        n = 1;
        while(n < size) n <<=1;
       data = vector<Val>(n<<1,calc.id());
    }
    explicit SegmentTree(vector<Val> &v){
        n = 1;
        while(n < v.size()) n <<=1;
        data = vector<Val> (n<<1);
        copy(v.begin(),v.end(),data.begin()+n);
        for(int i=n+(int)v.size(); i<2*n;i++)data[i]=calc.id();
        for(int i=n-1; i>0; --i){data[i]=calc(data[i*2],data[i*2+1]);}
    }
    void update(int i,Val v){
        int k = i + n;
        data[k] = v;
        for(k>>=1; k>=1; k>>=1)data[k] = calc(data[k*2],data[k*2+1]);
    }
    Val query(int l,int r){ //get[a,b)
        Val resl=calc.id(),resr=calc.id();
        for(l += n, r += n; l<r; l>>=1, r>>=1){
            if(l&1)resl=calc(resl, data[l++]);
            if(r&1)resr=calc(data[r-1],resr);
        }
        return calc(resl,resr);
    }
};
bool dfs(LL div, LL sum, int n, SegmentTree<GCD> &seg){
    bool can_win=false, is_odd=((sum/div-n)%2==1);
    for(int i=0; i<n && can_win==false ; ++i){
        bool f=false;
        if(seg.query(i,i+1)>div){
            seg.update(i,seg.query(i,i+1)-div);
            f=true;
        }
        LL g=seg.data[1];
        if((is_odd&&f) || g>div){
            can_win |= ! dfs(div*g,sum-div,n,seg);
        }
        if(f)seg.update(i,seg.query(i,i+1)+div);
    }
    return can_win;
}


