    int n; cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) cin >> a[i];
    if (n == 1) {
        if (a[0] != 1) return win(1);
        else return lose(1);
    }
    int cnt = 0;
    while (cnt++) {
        int sum = 0; bool one = false;
        for (int i = 0; i < n; i++) {
            sum += a[i] & 1;
            one |= a[i] == 1;
        }
        if (one) break;
        if (sum > 1) {
            if ((sum & 1) == (n & 1)) return lose(cnt&1);
            else return win(cnt&1);
        }
        while (true) {
            int s = 0;
            for (int i = 0; i < n; i++) {
                a[i] /= 2;
                s += a[i] & 1;
                s += a[i] == 1;
            }
            if (s) break;
        }
        bool end = false;
        for (int i = 0; i < n; i++) end |= a[i] == 1;
        if (end) {
            cnt++; break;
        }
    }
    int y = 0;
    for (int i = 0; i < n; i++) y = (y + a[i]) & 1;
    if ((n & 1) != y) return win(cnt&1);
    else return lose(cnt&1);
    return 0;
}

