template<class T = int> using V = vector<T>;
template<class T = int> using VV = V< V<T> >;

template<class Z> Z gcd(Z a, Z b) {
  while (b) swap(a %= b, b);
  return a;
}

int main() {
  cin.tie(nullptr); ios::sync_with_stdio(false);
  int n; cin >> n;
  V<> a(n); for (auto&& e : a) cin >> e;
  auto f = [&](const auto& f, V<> a) -> bool {
    int s = 0;
    for (int i = 0; i < n; ++i) {
      s ^= a[i] - 1 & 1;
    }
    if (s) return true;
    int odd = count_if(begin(a), end(a), [](int x) { return x & 1; });
    if (odd != 1) return false;
    for (int i = 0; i < n; ++i) if (a[i] & 1) {
      if (a[i] == 1) return false;
      --a[i];
    }
    int g = accumulate(begin(a), end(a), 0, gcd<int>);
    for (auto&& e : a) e /= g;
    return !f(f, a);
  };
  cout << (f(f, a) ? "First" : "Second") << '\n';
}

