        return 0;
    }
    assert(false);
    auto update = [&](){
        vec<int> L(N+1),R(N+1);
        for(int i=0;i<N;i++) L[i+1] = gcd(L[i],A[i]);
        for(int i=N-1;i>=0;i--) R[i] = gcd(R[i+1],A[i]);
        int g = -1,id = -1;
        for(int i=0;i<N;i++) if(A[i]!=1){
            int now = gcd(L[i],gcd(R[i+1],A[i]-1));
            if(now%2==0){
                g = now;
                id = i;
                break;
            }
        }
        if(id==-1) return false;
        for(int i=0;i<N;i++){
            if(i!=id) A[i] /= g;
            else A[i] = (A[i]-1)/g;
        }
        return true;
    };

    int c = 0;
    while(update()) c++;
    cout << ((sum+c-N)%2? "First\n":"Second\n");
}

