bool solve() {
  bool lost = true;
  for (int i = 0; i < N; ++i) {
    if (a[i] != 1) {
      lost = false;
    }
  }

  if (lost) {
    return false;
  }

  int cnt = 0;
  for (int i = 0; i < N; ++i) {
    if (a[i] % 2 == 0) {
      ++cnt;
    }
  }

  if (cnt & 1) {
    return true;
  }

  if (N - cnt > 1) {
    return false;
  }

  for (int i = 0; i < N; ++i) {
