      --a[i];
      break;
    }
  }

  int g = a[0];
  for (int i = 0; i < N; ++i) {
    g = gcd(g, a[i]);
  }

  for (int i = 0; i < N; ++i) {
    a[i] /= g;
  }

  return !solve();
}

