	long long here=count_moves(v);
	//cout<<here<<endl;

	if (here%2==1)
		return 1;

	vector<int> p_gcd;
	vector<int> s_gcd;
	p_gcd.resize(v.size()+1);
	s_gcd.resize(v.size()+1);
	for (int i=0;i<=v.size();i++)
	{
		p_gcd[i]=s_gcd[i]=0;
	}

	for (int i=0;i<v.size();i++)
	{
		p_gcd[i+1]=G(p_gcd[i],v[i]);
	}

	for (int i=v.size()-1;i>=0;--i)
	{
		s_gcd[i]=G(s_gcd[i],s_gcd[i+1]);
	}

	for (int i=0;i<v.size();i++)
	{
		if (v[i]==1)
			continue;
		int here=G(p_gcd[i],s_gcd[i+1]);
		here=G(here,v[i]-1);
		if (here==1)
			continue;
		vector<int> v2=v;
		v2[i]--;
		if (gcd(v2)==1)
			continue;
		v2=normalize(v2);
		if (guess_solver(v2)==-1)
			return 1;
	}
	return -1;
}

