#include <iostream>
#include <cmath>
#include <cstdio>
#include <cstring>
#include <algorithm>
#define rep(x, a, b) for(int x = (a); x <= (b); ++x)
#define rop(x, a, b) for(int x = (a); x < (b); ++x)
#define per(x, a, b) for(int x = (a); x >= (b); --x)
using namespace std;
typedef long long LL;
typedef double DB;
int a[100005], pr[100005], sf[100005];
int n;
int dfs() {
	int K = 0;
	rep(i, 1, n) K += a[i] - 1;
	if(K & 1) return 1;
	rep(i, 1, n) pr[i] = __gcd(pr[i - 1], a[i]);
	per(i, n, 1) sf[i] = __gcd(sf[i + 1], a[i]);
	rep(i, 1, n) {
		if(a[i] == 1) continue;
		K = __gcd(__gcd(pr[i - 1], sf[i + 1]), a[i] - 1);
		if(K != 1) {
			--a[i];
			rep(j, 1, n) a[j] /= K; 
			int bj = dfs();
			rep(j, 1, n) a[j] *= K;
			++a[i];
			return bj ^ 1;
		}
	}
	return 0;
}
int main() {
	scanf("%d", &n);
	rep(i, 1, n) {
		scanf("%d", &a[i]);
	}
	if(n == 1) puts("First");
	else if(n == 2);
	else puts(dfs() ? "First" : "Second");
	return 0;
}

#include <cstdio>
#include <algorithm>
#include <cstring>
using namespace std;

const int MAX_N = 100005;
int A[MAX_N], N;

int gcd(int x, int y) {
  return x == 0 ? y : gcd(y % x, x);
}

int work() {
  int even = 0, odd = 0;
  for (int i = 1; i <= N; ++i)
    if (A[i] & 1) odd++;
    else even++;
  if (even & 1)
    return 1;
  else if (!(even & 1) && odd > 1) 
    return 0;
  else {
    for (int i = 1; i <= N; ++i)
      if (A[i] & 1) {
	if (A[i] == 1) return 0;
	A[i]--;
	break;
      }
    int g = A[1];
    for (int i = 2; i <= N; ++i)
      g = gcd(g, A[i]);
    for (int i = 1; i <= N; ++i)
      A[i] /= g;
    return work() ^ 1;
  }
}

int main() {


  int T = 1;
  //scanf("%d", &T);

  while (T--) {
    scanf("%d", &N);
    for (int i = 1; i <= N; ++i)
      scanf("%d", &A[i]);
    bool flag = work();
    if (flag)
      puts("First");
    else
      puts("Second");
  }

  return 0;
}

#include <bits/stdc++.h>
using namespace std;

int nr[100010];
int n;

bool castiga()
{
    int x(0), y(0), py;
    for (int i(0); i < n; i++) {
        if (nr[i] & 1)
            y++, py = i;
        else
            x++;
    }
    if (x == 0) /// have to make even, oponent makes it odd again
        return false;
    if (x == 1) /// makes it odd, upper case
        return true;
    if (x % 2 == 0 && y > 1) /// if i change one of the evens, the oponent changes another
        return false;
    if (x % 2 == 1 && y > 1) /// oposite of upper case
        return false;

    /// i am here so y == 1
    if (nr[py] == 1) { /// gcd won't ever be != 1, so parity counts
        int stotal(0);
        for (int i(0); i < n; i++)
            stotal ^= nr[i] - 1;
        return (stotal & 1);
    }
    nr[py]--;
    for (int i(0); i < n; i++)
        nr[i] /= 2;
    return !castiga();
}

int main()
{
    cin >> n;
    for (int i(0); i < n; i++)
        cin >> nr[i];

    cout << (castiga() ? "First" : "Second");
    return 0;
}

