bool check() {
	bool ok = true;
	int br = 0;
	int pos = -1;
	for (int i=0 ; i<n ; i++) {
		br += a[i] % 2;
		if (a[i] % 2 and a[i]!=1) pos = i;
		if (a[i] > 1) ok = true;
	}
