inline bool dfs(){
    int cnt1, cnt2;
    cnt1 = cnt2 = 0;
    for(register int i = 1; i <= n; ++i){
        if(a[i] % 2 == 1){
            if(a[i] == 1)return 0;
            a[i]--;
            break;
        }
    }
    int GCD = a[1];
    for(register int i = 2; i <= n; ++i){
        GCD = __gcd(GCD, a[i]);
    }
    for(register int i = 1; i <= n; ++i){
        a[i] /= GCD;
    }
    for(register int i = 1; i <= n; ++i){
        if(a[i] % 2 == 0)cnt1++;
        else cnt2++;
    }
    if(cnt1 % 2 == 1)return 0;
    else{
        if(cnt2 > 1)return 1;
        else return dfs() ^ 1;
    }
}
