int a[100001];
int main()
{
	ios_base::sync_with_stdio(0); cin.tie(0);
	int n; cin>>n;
	int sum = 0;
	for(int i = 0; i < n; i++)
	{
		cin>>a[i];
		sum+=(a[i]&1);
		sum%=2;
	}
	int n2 = n%2;
	if(n==1)
	{
		if(sum==n2) p2();
		else p1();
		return 0;
	}
	if(sum!=n2)
	{
		p1();
		return 0;
	}
	int turn = 1;
	while(1)
	{
		int cntodd = 0;
		int ex1 = 0;
		int sm = 0;
		int oddidx=0;
		for(int i = 0; i < n; i++)
		{
			if(a[i]&1) 
			{
				cntodd++;
				oddidx=i;
			}
			if(a[i]==1) ex1++;
			sm+=(a[i]&1);
			sm%=2;
		}
		if(sm!=n2&&!turn)
		{
			p2();
			return 0;
		}
		if(sm!=n2&&turn)
		{
			p1();
			return 0;
		}
		if(ex1>0||cntodd>1)
		{
			if(sm!=n2) 
			{
				if(turn) p1();
				else p2();
			}
			else 
			{
				if(!turn) p1();
				else p2();
			}
			return 0;
		}
		a[oddidx]--;
		int gcd = a[0];
		for(int i = 0; i < n; i++)
		{
			gcd=__gcd(a[i],gcd);
		}
		assert(gcd>1);
		for(int i = 0; i < n; i++)
		{
			a[i]/=gcd;
		}
		turn^=1;
	}
}

