const int MAXN = 1e5 + 3;

int arr[MAXN], n;

bool solve() {
  bool flag = false;
  LL sum = 0, cnt = 0;
  for(int i = 0; i < n; i++) {
    if(arr[i] == 1) {
      flag = true;
    }
    cnt += arr[i] % 2;
    sum += arr[i] - 1;
  }
  if(flag) {
    return sum % 2 == 1;
  }
  if(cnt == 1) {
    for(int i = 0; i < n; i++) {
      arr[i] /= 2;
    }
    return !solve();
  }
  if((cnt % 2) != (n % 2)) return true;
  return false;
}

int main() {
  ios_base::sync_with_stdio(false);
  int n;
  cin >> n;
  for(int i = 0; i < n; i++) {
    cin >> arr[i];
  }
  bool ok;
  if(n == 1) {
    if(arr[0] % 2 == 0) ok = true;
    else ok = false;
  } else {
    ok = solve();
  }
  if(ok) {
    cout << "First" << endl;
  } else {
    cout << "Second" << endl;
  }
  return 0;
}

