using namespace std;

int win(bool a) {
    if (a) cout << "First\n";
    else cout << "Second\n";
    return 0;
}

int lose(bool a) {
    return win(!a);
}

int main() {
    cin.tie(nullptr);
    ios::sync_with_stdio(false);
    int n; cin >> n;
    vector<int> a(n);
    for (int i = 0; i < n; i++) cin >> a[i];
    if (n == 1) {
        if (a[0] != 1) return win(1);
        else return lose(1);
    }
    int cnt = 0;
    while (++cnt) {
        int sum = 0; bool one = false;
        for (int i = 0; i < n; i++) {
            sum += a[i] & 1;
            one |= a[i] == 1;
        }
        if (one) break;
        if (sum > 1) {
            if ((sum & 1) == (n & 1)) return lose(cnt&1);
            else return win(cnt&1);
        } else {
            if (!(n & 1)) return win(cnt&1);
        } 
        while (true) {
            int s = 0;
            for (int i = 0; i < n; i++) {
                a[i] /= 2;
                s += a[i] & 1;
            }
            if (s) break;
        }
    }
    int y = 0;
    for (int i = 0; i < n; i++) y = (y + a[i]) & 1;
    if ((n & 1) != y) return win(cnt&1);
    else return lose(cnt&1);
    return 0;
}

#include <bits/stdc++.h>

using namespace std;

const int  Nmax = 1e5 + 5;
int n, a[Nmax], i;

bool solve()
{
    int i, cnt = 0;
    for(i=1; i<=n; ++i)
        if(a[i] & 1) ++cnt;

    if(n % 2 == 0) return (cnt&1);
    if(cnt % 2 == 0) return 1;
    if(cnt > 2) return 0;

    for(i=1; i<=n; ++i)
        if(a[i] == 1) return 0;
            else if(a[i] & 1) --a[i];

    int g = a[1];
    for(i=2; i<=n; ++i)
        g = __gcd(g, a[i]);

    for(i=1; i<=n; ++i)
        a[i] /= g;

    return solve() ^ 1;
}

int main()
{
  //  freopen("input", "r", stdin);
    cin.sync_with_stdio(false);

    cin >> n;
    for(i=1; i<=n; ++i)
        cin >> a[i];

    //if(n == 1) cout << (a[i]&1 ? "Second" : "First") << '\n';
    cout << (solve() ? "First" : "Second") << '\n';

    return 0;
}

