bool tra() {
	int cnt = 0;
	rep(i, n) {
		if (a[i] % 2 == 0)cnt++;
	}
	if (cnt % 2)return true;
	else if (cnt != n - 1)return false;
	else {
		rep(i, n) {
			if (a[i] % 2) {
				if (a[i] == 1)return false;
				a[i]--;
