bool win(const vector<int>& v) {
	if (accumulate(v.begin(), v.end(), 0ll) % 2 != v.size() % 2) return true;
	int odd = 0;
	for (int i : v) {
		if (i % 2 == 1) ++odd;
	}

	if (odd > 1) return false;
	vector<int> w(v);
	for (int& i : w) {
		i /= 2;
	}

	return !win(w);
}

int main() {
	int N = Next<int>();
	auto A = inputVector<int>(N);
	puts(win(A) ? "First" : "Second");
	return 0;
}

