ll gcd(const ll a, const ll b){
    return a == 0 ? b : gcd(b%a, a); }

bool first_wins(vector<int>& v){
    static auto is_odd = [](const int x){ return x%2 == 1; };
    const int nr_odd = count_if(begin(v), end(v), is_odd);
    if(nr_odd%2 != (int)v.size()%2) return true;
    else if(nr_odd != 1) return false;
    else{
        --*find_if(begin(v), end(v), is_odd);
        const int full_gcd = accumulate(begin(v), end(v), v.front(), &gcd);
        transform(begin(v), end(v), begin(v), bind(divides<int>(), _1, full_gcd));
        return !first_wins(v); } }

int main(){
    int n;
    cin >> n;
    vector<int> v(n);
    for(auto& x : v) cin >> x;
    cout << (first_wins(v) ? "First" : "Second") << endl;
    return 0; }

