ll L[101010],R[101010];
ll S;

void solve() {
	int i,j,k,l,r,x,y; string s;
	int B[101];

	cin>>N;
	if(N==1) return _P("Second\n");

	FOR(i,N) {
		cin>>x;
		A[i]=x;
		S+=x;
	}

	if(N%2==0) {
		if(S%2==0) _P("Second\n");
		else _P("First\n");
	}
	else {
		if(S%2==1) {
			FOR(i,N) L[i+1]=__gcd(L[i],A[i]);
			for(i=N-1;i>=0;i--) R[i+1]=__gcd(R[i+2],A[i]);
			FOR(i,N) if(A[i]>1) {
				ll g=__gcd(__gcd(L[i],R[i+2]),A[i]-1);
				if((S-1)%g || g%2==0) continue;
				ll left=(S-1)/g;
				if(left%2==1) return _P("First\n");
			}
			return _P("Second\n");
		}
		else _P("First\n");
	}
}


