int main() {
	cin >> n;
	for (int i = 0; i < n; i++) cin >> a[i];
	if (n == 1) cout << "Second" << endl;
	else {
		int turn = 0; bool winning = false;
		while (true) {
			int s = 0;
			for (int i = 0; i < n; i++) s ^= a[i] ^ 1;
			if (s & 1) {
				winning = true;
				break;
			}
			int odd = 0, fv = 0;
			for (int i = 0; i < n; i++) {
				if (a[i] & 1) odd++, fv = a[i];
			}
			if (odd != 1 || fv == 1) break;
			for (int i = 0; i < n; i++) {
				if (a[i] & 1) a[i]--;
			}
			while (true) {
				bool f = false;
				for (int i = 0; i < n; i++) {
					a[i] >>= 1;
					if (a[i] & 1) f = true;
				}
				if (f) break;
			}
			turn ^= 1;
		}
		if (winning) cout << (turn ? "Second" : "First") << endl;
		else cout << (turn ? "First" : "Second") << endl;
	}
	return 0;
