int gcd(int a, int b) {
	return !b ? a : gcd(b, a % b);
}

bool win(const vector<int> &v) {
	int evens = 0;
	for(auto &each : v) {
		evens += each % 2 == 0;
	}
	if(evens & 1) {
		return true;
	}
	else if((int) v.size() - evens == 1) {
		vector<int> next_v = v;
		int gc = 0;
		for(auto &each : next_v) {
			if(each & 1) {
				each--;
			}
			gc = gcd(gc, each);
		}
		for(auto &each : next_v) {
			each /= gc;
		}
		return !win(next_v);
	}
	return false;
}

