inline int gcd(int a, int b) {
    int r;
    while (b) {
        r = a % b;
        a = b;
        b = r;
    }
    return a;
}

inline bool solve() {
    if (n == 1)
        return 0;
    int pare = 0;
    for (int i = 0; i < n; i++)
        pare += 1 - v[i] % 2;
    if (pare < n - 1)
        return pare % 2;
    if ((n - 2) % 2 == 0)
        return 1;
    for (int i = 0; i < n; i++)
