            v[i]--;
    int a = v[0];
    for (int i = 1; i < n; i++)
        a = gcd(v[i], a);
    if (a == 1)
        return 0;
    for (int i = 0; i < n; i++)
        v[i] /= a;
    return 1 - solve();
}

