bool calc1(int K){
	if(K==1)return false;
	K--;
	while(K%2==0)K/=2;
	return calc1(K)?false:true;
}

bool calc(vector<int> a){
	int s = 0;
	REP(i,a.size()){
		if(a[i]%2==0)s++;
	}
	if(s%2==1)return true;
	int ge = INF, odd_id = -1;
	REP(i,a.size()){
		if(a[i]%2==1){
			if(a[i]==1 or odd_id>=0)return false;
			odd_id = i;
			a[i]--;
		}
		int a_ = a[i], e = 0;
		while(a_%2==0)a_/=2, e++;
		ge = min(e,ge);
	}
	if(ge==0)return false;
	if(odd_id>=0){
		REP(i,a.size())a[i]/=(1<<ge);
		return calc(a)?false:true;
	}
	return false;
}

