const int maxn = 1e5 + 10;

int A[maxn];

int get0(long long x)
{
	int ret = 0;
	while (~x & 1ll)
	{
		x >>= 1;
		++ret;
	}
	return ret;
}

int N;
long long sum;

bool solve()
{
	sum = 0;
	for (int i = 0; i < N; ++i)
		sum += A[i];
	int id = -1;
	for (int i = 0; i < N; ++i)
	{
		if (A[i] & 1)
		{
			if (id == -1)
				id = i;
			else
				return false;
		}
	}
	if (id == -1)
		return false;
	if (A[id] == 1)
		return false;
	--A[id];
	int mi = 99;
	for (int i = 0; i < N; ++i)
		mi = min(mi, get0(A[i]));
	for (int i = 0; i < N; ++i)
		A[i] >>= mi;
	sum = 0;
	for (int i = 0; i < N; ++i)
		sum += A[i];
	if (~sum & 1)
		return false;
	return !solve();
}

int main()
{
	scanf("%d", &N);
	for (int i = 0; i < N; ++i)
		scanf("%d", &A[i]), sum += A[i];
	if (N & 1)
	{
		if (~sum & 1)
			puts("First");
		else
			puts(solve() ? "First" : "Second");
	}
	else
		puts((sum - N) & 1 ? "First" : "Second");
	return 0;
}

