int main() {
	scanf("%d", &n);
	long long tot = 0;
	for (int i = 1; i <= n; i++) scanf("%d", &a[i]), tot += a[i];
	if (n == 1) {
		if (a[1] == 1) second();
		else first();
	}
	if (n % 2 == 0) {
		if (tot % 2 == 1) {
			first();
		} else {
			second();
		}
	} else {
		int c = 0;
		while (tot % 2 == 1) {
			int pos = 0, cnt = 0;
			for (int i = 1; i <= n; i++) {
				if (a[i] % 2 == 1) {
					cnt++;
					pos = i;
				}
			}
			if (cnt > 1) break;
			assert(cnt != 0);
			if (a[pos] == 1) break;
			c ^= 1;
			a[pos]--;
			tot = 0;
			for (int i = 1; i <= n; i++) {
				tot += (a[i] /= 2);
			}
		}
		if (tot % 2 == 1) {
			c ^= 1;
		}
		if (c == 0) first();
		else second();
	}
	return 0;
}

