const long long N = (1e6 + 10);
const long long mod = (1e9 + 7);

int n,k,a[N],g;

int f(int sign){
    for(int i = 1; i <= n; i ++)
        k += (a[i] % 2);

    if((n - k) & 1)
        return sign;

    if(k > 1)
        return sign ^ 1;

    else{
        for(int i = 1; i <= n; i ++){
            if(a[i] == 1)
                return sign ^ 1;
            if(a[i] & 1)
                a[i] --;
            if(g == 0)
                g = a[i];
            else
                g = __gcd(g,a[i]);
        }
        int cnt = 0;
        for(int i = 1; i <= n; i ++){
            a[i] /= g;
            if(a[i] == 1)
                cnt ++;
        }
        if(cnt == n)
            return sign;
        return f(sign ^ 1);
    }
}
