{
	int s=0,mn=1e9;
	for(int i=1;i<=n;++i)s+=a[i]&1,mn=min(mn,a[i]);
	if((n-s)&1)return p;
	if(s>1||mn==1)return p^1;
	for(int i=1;i<=n;++i)a[i]>>=1;
	int d=a[1];
	for(int i=1;i<=n;++i)d=__gcd(d,a[i]);
	for(int i=1;i<=n;++i)a[i]/=d;
	calc(p^1);
}
