template<class T> T gcd(T a, T b) { return b ? gcd(b, a % b) : a; }
template<class T> T lcm(T a, T b) { return a / gcd(a, b) * b; }
ll mod_pow(ll a, ll n, ll mod) {
	ll ret = 1;
	ll p = a % mod;
	while (n) {
		if (n & 1) ret = ret * p % mod;
		p = p * p % mod;
		n >>= 1;
	}
	return ret;
}
template<class T> T extgcd(T a, T b, T& x, T& y) { for (T u = y = 1, v = x = 0; a;) { T q = b / a; swap(x -= q * u, u); swap(y -= q * v, v); swap(b -= q * a, a); } return b; }
template<class T> T mod_inv(T a, T m) { T x, y; extgcd(a, m, x, y); return (m + x % m) % m; }

int memo[101][101][101];
int dfs(int a, int b, int c) {
	int& ret = memo[a][b][c];
	if (ret != -1) return ret;
	int g = gcd(a, gcd(b, c));
	if (g != 1) {
		return ret = dfs(a / g, b / g, c / g);
	}
	ret = 0;
	if (a > 1 && dfs(a - 1, b, c) == 0) ret = 1;
	if (b > 1 && dfs(a , b - 1, c) == 0) ret = 1;
	if (c > 1 && dfs(a, b, c - 1) == 0) ret = 1;

	return ret;
}

bool dfs(vector<int>& a) {
	ll sm = 0;
	FOR(i, sz(a)) {
		sm += a[i];
	}
	if ((sm - sz(a)) % 2 != 0) return true;

	vector<int> g1(sz(a)), g2(sz(a));
	g1[0] = a[0];
	for (int i = 1; i < sz(a); i++) {
		g1[i] = gcd(g1[i - 1], a[i]);
	}

	g2[sz(a) - 1] = a[sz(a) - 1];
	for (int i = sz(a) - 2; i >= 0; i--) {
		g2[i] = gcd(g2[i + 1], a[i]);
	}
	FOR(i, sz(a)) {
		if (a[i] == 1) continue;
		int gg;
		if (i == 0) gg = g2[1];
		else if (i == sz(a) - 1) gg = g1[sz(a) - 2];
		else {
			gg = gcd(g1[i - 1], g2[i + 1]);
		}
		gg = gcd(gg, a[i] - 1);
		if (gg != 1) {
			vector<int> nw = a;
			FOR(j, sz(a)) {
				if (i != j) nw[j] /= gg;
				else nw[j] = (nw[j] - 1) / gg;
			}
			bool bb = dfs(nw);
			if (!bb) return true;
		}
	}

	return false;
}

