int query(){
	a = 0;
	for (int i=1;i<=N;++i)a += A[i]-1;
	if (a%2 == 1){
		return 1;
	}
	// Other case is if everything except 1 thing is even
	int ind = -1;
	for (int i=1;i<=N;++i){
		if (A[i]%2 == 1){
			if (ind == -1)ind = i;
			else {
				return 2;
			}
		}
	}
	if (ind == -1){return 2;}
	--A[ind];
	int l = A[1];
	for (int i=2;i<=N;++i)l = gcd(l, A[i]);
	assert(l != 1);

	for (int i=1;i<=N;++i)A[i] /= l;
	return 3 - query();
}

