bool game(vector<int> v) {
    static map<vector<int>, bool> dp;
    if (dp.count(v)) return dp[v];

    bool ret = false;
    for (int i = 0; i < v.size(); ++i) {
        if (v[i] > 1) {
            --v[i];
            auto w = reduc(v);
            if (!game(v)) ret = true;
            ++v[i];
        }
    }
    return dp[v] = ret;
}

