using namespace std;
typedef long long ll;

int N;
ll A[100000];

template <typename T>
T gcd(T a, T b){
    if(a < b) swap(a, b);
    while(b != 0){
        T tmp = b;
        b = a%b;
        a = tmp;
    }
    return a;
}

int cntOdd(){
    int ans  = 0;
    for(int i = 0; i < N; i++){
        if(A[i]%2 == 1) ans++;
    }
    return ans;
}

ll cntRem(){
    ll ans  = 0;
    for(int i = 0; i < N; i++){
        ans += (A[i]-1);
    }
    return ans;
}

int gcd_all(){
    int ans = gcd<int>(A[0], A[1]);
    for(int i = 2; i < N; i++){
        ans = gcd<int>(ans, A[i]);
    }
    return ans;
}

int oddIdx(){
    for(int i = 0; i < N; i++){
        return i;
    }
}

void process(){
    int g = gcd_all();
    for(int i = 0; i < N; i++) A[i] /= g;
}

bool oneExists(){
    for(int i = 0; i < N; i++){
        if(A[i] == 1) return true;
    }
    return false;
}

string ans;

void judge(int cur){
    // cout << "judge()" << endl;
    // for(int i = 0; i < N; i++) cout << A[i] << ' ';
    // cout << endl;
    ll rem = cntRem();
    if(oneExists()){
        if(rem%2 == 1){
            ans = cur == 0 ? "First" : "Second";
        }else{
            ans = cur == 0 ? "Second" : "First";
            return;
        }
    }
    if(rem%2 == 1){
        ans = cur == 0 ? "First" : "Second";
        return;
    }
    if(cntOdd() >= 2){
        ans = cur == 0 ? "Second" : "First";
        return;
    }
    int idx = oddIdx();
    A[idx]--;
    process();
    judge(cur^1);
}

int main(){
    ios::sync_with_stdio(false);
    cin.tie(0);
    cout << setprecision(10) << fixed;
    cin >> N;
    for(int i = 0; i < N; i++) cin >> A[i];
    if(N == 1) {
        cout << "First" << endl;
        return 0;
    }
    judge(0);
    cout << ans << endl;
}

