using namespace std;
int gcd(int a, int b) {
	int c;
	while (a != 0) { c = a; a = b%a; b = c; }
	return b;
}
bool f(vector<int> a) {
	if (a[0] == 1) {
		int sum = 0;
		for (int x : a) sum += (x-1) % 2;
		return sum % 2 != 0;
	} else {
		int c_odd = 0, c_even = 0;
		for (int x : a) ++(x % 2 ? c_odd : c_even);
		if (c_even % 2 != 0) {
			return true;
		} else {
			if (c_odd == 0) throw 1;
			if (c_odd >= 2) {
				return false;
			} else {
				auto b = a;
				for (auto& x : b) if (x % 2) {
					--x;
					break;
				}
				int g = b[0];
				for (auto x : b) g = gcd(g, x);
				for (auto& x : b) x /= g;
				return !f(b);
			}
		}
	}
}
int main() {
	int N;
	cin >> N;
	vector<int> A(N);
	for (auto& x : A) cin >> x;
	sort(A.begin(), A.end());
	cout << (f(A) ? "First" : "Second") << endl;
}
