bool win(const vector<int> &v) {
	int evens = 0;
	for(auto &each : v) {
		evens += each % 2 == 0;
	}
	if(evens & 1) {
		return true;
	}
	else if((int) v.size() - evens == 1) {
		vector<int> next_v = v;
		int gc = 0;
		for(auto &each : next_v) {
			if(each & 1) {
				if(each == 1) {
					return false;
				}
				each--;
			}
			gc = gcd(gc, each);
		}
		for(auto &each : next_v) {
			each /= gc;
		}
		return !win(next_v);
	}
	return false;
}

